"use client";

import { useCallback, useEffect, useState } from "react";

import { RotateCw, Save, X } from "lucide-react";
import { toast } from "sonner";

import ColorInput from "./color-input";
import Logo from "./logo";

interface ThemeVariable {
  name: string;
  value: string;
}

interface ThemeGroup {
  title: string;
  variables: ThemeVariable[];
}

export default function ThemeEditor() {
  const [isOpen, setIsOpen] = useState(false);
  const [theme, setTheme] = useState<"light" | "dark">("light");
  const [groups, setGroups] = useState<ThemeGroup[]>([]);
  const [originalCss, setOriginalCss] = useState("");

  const tokenGroups = {
    "Background & Text": [
      "background",
      "foreground",
      "card",
      "card-foreground",
      "popover",
      "popover-foreground",
    ],
    "Cards & Surfaces": [
      "muted",
      "muted-foreground",
      "accent",
      "accent-foreground",
    ],
    "Interactive Elements": [
      "primary",
      "primary-foreground",
      "secondary",
      "secondary-foreground",
    ],
    "Forms & States": [
      "destructive",
      "destructive-foreground",
      "input",
      "ring",
      "border",
    ],
    Charts: ["chart-1", "chart-2", "chart-3", "chart-4", "chart-5"],
    Sidebar: [
      "sidebar-background",
      "sidebar-foreground",
      "sidebar-primary",
      "sidebar-primary-foreground",
      "sidebar-accent",
      "sidebar-accent-foreground",
      "sidebar-border",
      "sidebar-ring",
    ],
  };

  const extractCSSVariables = useCallback((css: string, selector: string) => {
    const vars = new Map<string, string>();
    const regex = new RegExp(`${selector}\\s*\\{([^}]+)\\}`, "s");
    const match = css.match(regex);

    if (match?.[1]) {
      const lines = match[1].split(";");
      for (const line of lines) {
        const [key, value] = line.split(":").map((s) => s.trim());
        if (key?.startsWith("--") && value) {
          vars.set(key, value);
        }
      }
    }

    return vars;
  }, []);

  const loadThemeVariables = useCallback(async () => {
    try {
      const response = await fetch("/api/tinte/read-globals");
      const data = await response.json();

      if (!data.success) {
        toast.error("Failed to load theme variables");
        return;
      }

      setOriginalCss(data.content);

      const rootVars = extractCSSVariables(data.content, ":root");
      const darkVars = extractCSSVariables(data.content, ".dark");

      const currentVars = theme === "light" ? rootVars : darkVars;

      const newGroups: ThemeGroup[] = Object.entries(tokenGroups).map(
        ([title, varNames]) => ({
          title,
          variables: varNames
            .map((name) => {
              const cssVar = `--${name}`;
              const value = currentVars.get(cssVar) || "";
              return { name, value };
            })
            .filter((v) => v.value),
        }),
      );

      setGroups(newGroups);
    } catch (error) {
      toast.error("Error loading theme variables");
      console.error(error);
    }
  }, [theme, extractCSSVariables]);

  useEffect(() => {
    if (isOpen) {
      loadThemeVariables();
    }
  }, [isOpen, loadThemeVariables]);

  const handleColorChange = (
    groupIndex: number,
    varIndex: number,
    newValue: string,
  ) => {
    setGroups((prev) => {
      const updated = [...prev];
      updated[groupIndex].variables[varIndex].value = newValue;
      return updated;
    });

    const cssVar = `--${groups[groupIndex].variables[varIndex].name}`;
    const root =
      theme === "light" ? document.documentElement : document.documentElement;

    if (theme === "dark") {
      root.classList.add("dark");
    }

    root.style.setProperty(cssVar, newValue);
  };

  const handleSave = async () => {
    try {
      let updatedCss = originalCss;

      const rootVars = new Map<string, string>();
      const darkVars = new Map<string, string>();

      for (const group of groups) {
        for (const variable of group.variables) {
          const cssVar = `--${variable.name}`;
          if (theme === "light") {
            rootVars.set(cssVar, variable.value);
          } else {
            darkVars.set(cssVar, variable.value);
          }
        }
      }

      if (theme === "light") {
        updatedCss = updateCSSBlock(updatedCss, ":root", rootVars);
      } else {
        updatedCss = updateCSSBlock(updatedCss, ".dark", darkVars);
      }

      const response = await fetch("/api/tinte/write-globals", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ content: updatedCss }),
      });

      const data = await response.json();

      if (data.success) {
        toast.success("Theme saved successfully!");
        setOriginalCss(updatedCss);
      } else {
        toast.error("Failed to save theme");
      }
    } catch (error) {
      toast.error("Error saving theme");
      console.error(error);
    }
  };

  const updateCSSBlock = (
    css: string,
    selector: string,
    vars: Map<string, string>,
  ) => {
    const regex = new RegExp(`(${selector}\\s*\\{)([^}]+)(\\})`, "s");
    const match = css.match(regex);

    if (!match) return css;

    const existingVars = new Map<string, string>();
    const lines = match[2].split(";");

    for (const line of lines) {
      const [key, value] = line.split(":").map((s) => s.trim());
      if (key?.startsWith("--") && value) {
        existingVars.set(key, value);
      }
    }

    for (const [key, value] of vars) {
      existingVars.set(key, value);
    }

    const newBlock = Array.from(existingVars.entries())
      .map(([key, value]) => `    ${key}: ${value};`)
      .join("\n");

    return css.replace(regex, `${match[1]}\n${newBlock}\n  ${match[3]}`);
  };

  return (
    <>
      <button
        type="button"
        onClick={() => setIsOpen(true)}
        className="fixed bottom-6 right-6 z-50 flex h-14 w-14 items-center justify-center rounded-full bg-gradient-to-br from-purple-500 via-pink-500 to-orange-500 shadow-lg transition-transform hover:scale-110 active:scale-95"
        aria-label="Open theme editor"
      >
        <Logo size={28} className="text-white" />
      </button>

      {isOpen && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/50 p-4">
          <div className="relative w-full max-w-2xl max-h-[90vh] overflow-hidden rounded-lg border bg-background shadow-2xl">
            <div className="sticky top-0 z-10 flex items-center justify-between border-b bg-background px-6 py-4">
              <div className="flex items-center gap-3">
                <Logo size={32} />
                <h2 className="text-xl font-semibold">Theme Editor</h2>
              </div>
              <div className="flex items-center gap-2">
                <button
                  type="button"
                  onClick={() => setTheme(theme === "light" ? "dark" : "light")}
                  className="rounded-md border px-3 py-1.5 text-sm font-medium transition-colors hover:bg-accent"
                >
                  {theme === "light" ? "üåô Dark" : "‚òÄÔ∏è Light"}
                </button>
                <button
                  type="button"
                  onClick={loadThemeVariables}
                  className="rounded-md border p-2 transition-colors hover:bg-accent"
                  aria-label="Reload theme"
                >
                  <RotateCw className="h-4 w-4" />
                </button>
                <button
                  type="button"
                  onClick={handleSave}
                  className="rounded-md bg-primary px-3 py-2 text-sm font-medium text-primary-foreground transition-colors hover:bg-primary/90"
                >
                  <Save className="h-4 w-4" />
                </button>
                <button
                  type="button"
                  onClick={() => setIsOpen(false)}
                  className="rounded-md border p-2 transition-colors hover:bg-accent"
                  aria-label="Close theme editor"
                >
                  <X className="h-4 w-4" />
                </button>
              </div>
            </div>

            <div
              className="overflow-y-auto p-6"
              style={{ maxHeight: "calc(90vh - 80px)" }}
            >
              {groups.map((group, groupIndex) => (
                <div key={group.title} className="mb-6">
                  <h3 className="mb-3 text-sm font-semibold text-muted-foreground">
                    {group.title}
                  </h3>
                  <div className="space-y-3">
                    {group.variables.map((variable, varIndex) => (
                      <div
                        key={variable.name}
                        className="flex items-center gap-3"
                      >
                        <label
                          htmlFor={`var-${groupIndex}-${varIndex}`}
                          className="min-w-[200px] text-sm font-mono"
                        >
                          --{variable.name}
                        </label>
                        <ColorInput
                          id={`var-${groupIndex}-${varIndex}`}
                          value={variable.value}
                          onChange={(newValue) =>
                            handleColorChange(groupIndex, varIndex, newValue)
                          }
                        />
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
}

"use client";

import { useCallback, useEffect, useRef, useState } from "react";

import type { HsvaColor } from "@uiw/color-convert";
import {
  hexToHsva,
  hsvaToHex,
  hsvaToHslString,
  hsvaToRgbaString,
} from "@uiw/color-convert";
import Wheel from "@uiw/react-color-wheel";
import { formatCss, formatHex, parse } from "culori";

interface ColorInputProps {
  id?: string;
  value: string;
  onChange: (value: string) => void;
}

type ColorFormat = "hex" | "rgb" | "hsl" | "oklch" | "lch";

export default function ColorInput({ id, value, onChange }: ColorInputProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [hsva, setHsva] = useState<HsvaColor>({ h: 0, s: 0, v: 100, a: 1 });
  const [format, setFormat] = useState<ColorFormat>("hex");
  const containerRef = useRef<HTMLDivElement>(null);

  const detectFormat = useCallback((colorValue: string): ColorFormat => {
    const trimmed = colorValue.trim();
    if (trimmed.startsWith("oklch(")) return "oklch";
    if (trimmed.startsWith("lch(")) return "lch";
    if (trimmed.startsWith("rgb(") || trimmed.startsWith("rgba(")) return "rgb";
    if (trimmed.startsWith("hsl(") || trimmed.startsWith("hsla(")) return "hsl";
    return "hex";
  }, []);

  useEffect(() => {
    const detectedFormat = detectFormat(value);
    setFormat(detectedFormat);

    const parsedColor = parse(value.trim());
    if (parsedColor) {
      const hex = formatHex(parsedColor);
      const newHsva = hexToHsva(hex);
      setHsva(newHsva);
    }
  }, [value, detectFormat]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener("mousedown", handleClickOutside);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [isOpen]);

  const handleColorChange = (newHsva: HsvaColor) => {
    setHsva(newHsva);

    let formattedValue: string;

    switch (format) {
      case "hex": {
        formattedValue = hsvaToHex(newHsva);
        break;
      }
      case "rgb": {
        formattedValue = hsvaToRgbaString(newHsva);
        break;
      }
      case "hsl": {
        formattedValue = hsvaToHslString(newHsva);
        break;
      }
      case "oklch":
      case "lch": {
        const hex = hsvaToHex(newHsva);
        const parsed = parse(hex);
        if (parsed) {
          formattedValue =
            formatCss(
              format === "oklch"
                ? { mode: "oklch", ...parsed }
                : { mode: "lch", ...parsed },
            ) || value;
        } else {
          formattedValue = value;
        }
        break;
      }
      default:
        formattedValue = hsvaToHex(newHsva);
    }

    onChange(formattedValue);
  };

  const handleFormatToggle = () => {
    const formats: ColorFormat[] = ["hex", "rgb", "hsl", "oklch", "lch"];
    const currentIndex = formats.indexOf(format);
    const nextFormat = formats[(currentIndex + 1) % formats.length];
    setFormat(nextFormat);

    let formattedValue: string;

    switch (nextFormat) {
      case "hex": {
        formattedValue = hsvaToHex(hsva);
        break;
      }
      case "rgb": {
        formattedValue = hsvaToRgbaString(hsva);
        break;
      }
      case "hsl": {
        formattedValue = hsvaToHslString(hsva);
        break;
      }
      case "oklch": {
        const hex = hsvaToHex(hsva);
        const parsed = parse(hex);
        if (parsed) {
          formattedValue = formatCss({ mode: "oklch", ...parsed }) || value;
        } else {
          formattedValue = value;
        }
        break;
      }
      case "lch": {
        const hex = hsvaToHex(hsva);
        const parsed = parse(hex);
        if (parsed) {
          formattedValue = formatCss({ mode: "lch", ...parsed }) || value;
        } else {
          formattedValue = value;
        }
        break;
      }
      default:
        formattedValue = hsvaToHex(hsva);
    }

    onChange(formattedValue);
  };

  const previewColor = hsvaToHex(hsva);

  return (
    <div ref={containerRef} className="relative flex items-center gap-2">
      <button
        type="button"
        onClick={() => setIsOpen(!isOpen)}
        className="h-8 w-8 rounded border shadow-sm transition-transform hover:scale-110"
        style={{ backgroundColor: previewColor }}
        aria-label="Open color picker"
      />
      <input
        type="text"
        id={id}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="flex-1 rounded-md border bg-background px-3 py-1.5 text-sm font-mono"
      />
      <button
        type="button"
        onClick={handleFormatToggle}
        className="rounded-md border px-3 py-1.5 text-xs font-medium uppercase transition-colors hover:bg-accent"
      >
        {format}
      </button>

      {isOpen && (
        <div className="absolute left-0 top-12 z-50 rounded-lg border bg-popover p-4 shadow-lg">
          <Wheel
            color={hsva}
            onChange={(color) => handleColorChange(color.hsva)}
            width={200}
            height={200}
          />
        </div>
      )}
    </div>
  );
}

import { readFile } from "node:fs/promises";
import { join } from "node:path";

import { NextResponse } from "next/server";

export async function GET() {
  try {
    const globalsPath = join(process.cwd(), "app", "globals.css");
    const content = await readFile(globalsPath, "utf-8");

    return NextResponse.json({ success: true, content });
  } catch (error) {
    console.error("Error reading globals.css:", error);
    return NextResponse.json(
      { success: false, error: "Failed to read globals.css" },
      { status: 500 },
    );
  }
}

import { writeFile } from "node:fs/promises";
import { join } from "node:path";

import { NextResponse } from "next/server";

export async function POST(request: Request) {
  try {
    const { content } = await request.json();

    if (typeof content !== "string") {
      return NextResponse.json(
        { success: false, error: "Invalid content" },
        { status: 400 },
      );
    }

    const globalsPath = join(process.cwd(), "app", "globals.css");
    await writeFile(globalsPath, content, "utf-8");

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error("Error writing globals.css:", error);
    return NextResponse.json(
      { success: false, error: "Failed to write globals.css" },
      { status: 500 },
    );
  }
}
