import { RevokeInvitationDialog } from "./revoke-invitation-dialog";
import { getRoleLabel } from "../../lib/constants";
import type { OrganizationInvitationResource } from "@clerk/types";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface InvitationsTableProps {
  labels?: {
    email?: string;
    role?: string;
    sentDate?: string;
    actions?: string;
    openMenu?: string;
    revokeInvitation?: string;
    invitationRevoked?: string;
    revokeError?: string;
    noInvitations?: string;
  };
  roleLabels?: {
    admin?: string;
    member?: string;
  };
}

function InvitationsTableLoading() {
  return (
    <>
      {[1, 2].map((i) => (
        <TableRow key={i}>
          <TableCell>
            <Skeleton className="h-4 w-48" />
          </TableCell>
          <TableCell>
            <Skeleton className="h-5 w-16" />
          </TableCell>
          <TableCell>
            <Skeleton className="h-4 w-24" />
          </TableCell>
          <TableCell>
            <Skeleton className="h-8 w-8" />
          </TableCell>
        </TableRow>
      ))}
    </>
  );
}

export function InvitationsTable({
  labels = {},
  roleLabels,
}: InvitationsTableProps) {
  const { invitations, isLoaded } = useOrganization({
    invitations: { infinite: true },
  });

  const [revokingId, setRevokingId] = useState<string | null>(null);
  const [invitationToRevoke, setInvitationToRevoke] =
    useState<OrganizationInvitationResource | null>(null);

  const mergedLabels = {
    email: "Email",
    role: "Role",
    sentDate: "Sent date",
    actions: "Actions",
    openMenu: "Open menu",
    revokeInvitation: "Revoke invitation",
    invitationRevoked: "Invitation revoked successfully",
    revokeError: "Error revoking invitation",
    noInvitations: "No pending invitations",
    ...labels,
  };

  const isLoading = !isLoaded || !invitations?.data;
  const hasInvitations = invitations?.data && invitations.data.length > 0;

  const handleRevokeInvitation = async () => {
    if (!invitationToRevoke) return;

    try {
      setRevokingId(invitationToRevoke.id);
      await invitationToRevoke.revoke();
      await invitations?.revalidate?.();
      toast.success(mergedLabels.invitationRevoked);
    } catch (error) {
      console.error("Error revoking invitation:", error);
      toast.error(mergedLabels.revokeError);
    } finally {
      setRevokingId(null);
      setInvitationToRevoke(null);
    }
  };

  return (
    <div data-slot="invitations-table" className="w-full">
      <div className="overflow-hidden rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>{mergedLabels.email}</TableHead>
              <TableHead>{mergedLabels.role}</TableHead>
              <TableHead>{mergedLabels.sentDate}</TableHead>
              <TableHead className="text-right"></TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {isLoading ? (
              <InvitationsTableLoading />
            ) : hasInvitations ? (
              invitations.data.map((invitation) => (
                <TableRow key={invitation.id}>
                  <TableCell className="font-medium">
                    <div className="flex items-center gap-2">
                      <Mail className="h-3.5 w-3.5 shrink-0 text-muted-foreground" />
                      <span className="text-sm">{invitation.emailAddress}</span>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline">
                      {getRoleLabel(invitation.role, roleLabels)}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <span className="text-sm">
                      {format(new Date(invitation.createdAt), "PPP")}
                    </span>
                  </TableCell>
                  <TableCell className="text-right">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button
                          variant="ghost"
                          size="icon"
                          className="h-8 w-8 cursor-pointer"
                          disabled={revokingId === invitation.id}
                          onClick={(e) => e.stopPropagation()}
                        >
                          <span className="sr-only">{mergedLabels.openMenu}</span>
                          <MoreHorizontal className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuLabel>{mergedLabels.actions}</DropdownMenuLabel>
                        <DropdownMenuSeparator />
                        <DropdownMenuItem
                          className="cursor-pointer text-destructive"
                          onClick={(e) => {
                            e.stopPropagation();
                            setInvitationToRevoke(invitation);
                          }}
                          disabled={revokingId === invitation.id}
                        >
                          <XCircle className="mr-2 h-4 w-4" />
                          {mergedLabels.revokeInvitation}
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={4} className="py-8 text-center text-muted-foreground">
                  {mergedLabels.noInvitations}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      <RevokeInvitationDialog
        invitation={invitationToRevoke}
        onOpenChange={(open) => !open && setInvitationToRevoke(null)}
        onConfirm={handleRevokeInvitation}
      />
    </div>
  );
}

"use client";

import * as React from "react";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import * as z from "zod";
import { UserPlus, X, Send } from "lucide-react";
import { useOrganization } from "@clerk/nextjs";
import { toast } from "sonner";
import { ORG_ROLES } from "../../lib/constants";
import { useRole } from "../../lib/use-role";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

const formSchema = z.object({
  email: z.string().email({
    message: "Please enter a valid email address.",
  }),
  role: z.enum([ORG_ROLES.ADMIN, ORG_ROLES.MEMBER]),
});

interface InviteDialogProps {
  labels?: {
    trigger?: string;
    title?: string;
    description?: string;
    emailLabel?: string;
    emailPlaceholder?: string;
    roleLabel?: string;
    rolePlaceholder?: string;
    roleAdmin?: string;
    roleMember?: string;
    cancel?: string;
    submit?: string;
    submitting?: string;
    success?: string;
    error?: string;
  };
}

export function InviteDialog({ labels = {} }: InviteDialogProps) {
  const { isAdmin } = useRole();
  const [open, setOpen] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(false);
  const { organization, invitations } = useOrganization({
    invitations: { infinite: true },
  });

  const mergedLabels = {
    trigger: "Invite new member",
    title: "Invite new member",
    description:
      "Enter the email address of the person you want to invite to the organization. We will send them an invitation email.",
    emailLabel: "Email address",
    emailPlaceholder: "example@email.com",
    roleLabel: "Role",
    rolePlaceholder: "Select a role",
    roleAdmin: "Admin",
    roleMember: "Member",
    cancel: "Cancel",
    submit: "Send invitation",
    submitting: "Sending...",
    success: "Invitation sent successfully",
    error: "Error sending invitation. Please try again.",
    ...labels,
  };

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: "",
    },
  });

  if (!isAdmin) return null;

  async function onSubmit(values: z.infer<typeof formSchema>) {
    if (!organization) return;

    try {
      setIsLoading(true);
      await organization.inviteMember({
        emailAddress: values.email,
        role: values.role,
      });
      await invitations?.revalidate?.();

      form.reset();
      setOpen(false);
      toast.success(mergedLabels.success);
    } catch (error) {
      console.error("Error inviting member:", error);
      toast.error(mergedLabels.error);
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button className="cursor-pointer gap-2">
          <UserPlus className="h-4 w-4" />
          {mergedLabels.trigger}
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>{mergedLabels.title}</DialogTitle>
          <DialogDescription>{mergedLabels.description}</DialogDescription>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{mergedLabels.emailLabel}</FormLabel>
                  <FormControl>
                    <Input
                      placeholder={mergedLabels.emailPlaceholder}
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="role"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{mergedLabels.roleLabel}</FormLabel>
                  <Select
                    onValueChange={field.onChange}
                    defaultValue={field.value}
                  >
                    <FormControl>
                      <SelectTrigger className="w-full">
                        <SelectValue placeholder={mergedLabels.rolePlaceholder} />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value={ORG_ROLES.MEMBER}>
                        {mergedLabels.roleMember}
                      </SelectItem>
                      <SelectItem value={ORG_ROLES.ADMIN}>
                        {mergedLabels.roleAdmin}
                      </SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
            <DialogFooter>
              <Button
                type="button"
                variant="outline"
                onClick={() => setOpen(false)}
                disabled={isLoading}
                className="cursor-pointer"
              >
                <X className="h-4 w-4" />
                {mergedLabels.cancel}
              </Button>
              <Button
                type="submit"
                disabled={isLoading}
                className="cursor-pointer"
              >
                <Send className="h-4 w-4" />
                {isLoading ? mergedLabels.submitting : mergedLabels.submit}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

