    <span
      data-slot="breadcrumb-page"
      role="link"
      aria-disabled="true"
      aria-current="page"
      className={cn("font-normal text-foreground", className)}
      {...props}
    />
  );
}

function BreadcrumbSeparator({
  children,
  className,
  ...props
}: React.ComponentProps<"li">) {
  return (
    <li
      data-slot="breadcrumb-separator"
      role="presentation"
      aria-hidden="true"
      className={cn("[&>svg]:size-3.5", className)}
      {...props}
    >
      {children ?? <ChevronRight />}
    </li>
  );
}

function BreadcrumbEllipsis({
  className,
  ...props
}: React.ComponentProps<"span">) {
  return (
    <span
      data-slot="breadcrumb-ellipsis"
      role="presentation"
      aria-hidden="true"
      className={cn("flex size-9 items-center justify-center", className)}
      {...props}
    >
      <MoreHorizontal className="size-4" />
      <span className="sr-only">More</span>
    </span>
  );
}

export {
  Breadcrumb,
  BreadcrumbList,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbPage,
  BreadcrumbSeparator,
  BreadcrumbEllipsis,
};

/**
 * Clerk organization role constants
 * These are the standard Clerk organization roles
 */
export const ORG_ROLES = {
  ADMIN: "org:admin",
  MEMBER: "org:member",
} as const;

export type OrgRole = (typeof ORG_ROLES)[keyof typeof ORG_ROLES];

/**
 * Get a human-readable label for an organization role
 * @param role - The Clerk organization role
 * @param labels - Custom labels for roles (optional)
 * @returns Human-readable role label
 */
export function getRoleLabel(
  role: string | undefined | null,
  labels?: { admin?: string; member?: string }
): string {
  const defaultLabels = {
    admin: "Admin",
    member: "Member",
  };
  const mergedLabels = { ...defaultLabels, ...labels };

  if (role === ORG_ROLES.ADMIN) return mergedLabels.admin;
  if (role === ORG_ROLES.MEMBER) return mergedLabels.member;
  return "Unknown";
}

"use client";

import { useOrganization } from "@clerk/nextjs";
import { ORG_ROLES } from "./constants";

/**
 * Hook to get the current user's role in the organization
 * @returns Object containing the role string and isAdmin boolean
 */
export function useRole() {
  const { membership } = useOrganization();

  const role = membership?.role;
  const isAdmin = role === ORG_ROLES.ADMIN;

  return {
    role,
    isAdmin,
  };
}

"use client";

import * as React from "react";

const MOBILE_BREAKPOINT = 768;

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(
    undefined
  );

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    };
    mql.addEventListener("change", onChange);
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    return () => mql.removeEventListener("change", onChange);
  }, []);

  return !!isMobile;
}
