"use client";

import { useCallback, useEffect, useState } from "react";

import { useChat } from "@ai-sdk/react";
import { DefaultChatTransport } from "ai";
import { formatHex, oklch } from "culori";
import { RefreshCw } from "lucide-react";

import { ChatInput } from "./chat/chat-input";
import { Message } from "./chat/message";
import { ColorInput } from "./color-input";
import Logo from "./logo";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "./ui/accordion";
import { Button } from "./ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "./ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";
import { Textarea } from "./ui/textarea";

type ShadcnTokens = Record<string, string>;

interface ShadcnTheme {
  light: ShadcnTokens;
  dark: ShadcnTokens;
}

// Token groups for organized UI
const TOKEN_GROUPS = [
  {
    label: "Background & Text",
    tokens: ["background", "foreground", "muted", "muted-foreground"],
  },
  {
    label: "Cards & Surfaces",
    tokens: ["card", "card-foreground", "popover", "popover-foreground"],
  },
  {
    label: "Interactive Elements",
    tokens: [
      "primary",
      "primary-foreground",
      "secondary",
      "secondary-foreground",
      "accent",
      "accent-foreground",
    ],
  },
  {
    label: "Forms & States",
    tokens: [
      "border",
      "input",
      "ring",
      "destructive",
      "destructive-foreground",
    ],
  },
  {
    label: "Charts",
    tokens: ["chart-1", "chart-2", "chart-3", "chart-4", "chart-5"],
  },
  {
    label: "Sidebar",
    tokens: [
      "sidebar-background",
      "sidebar-foreground",
      "sidebar-primary",
      "sidebar-primary-foreground",
      "sidebar-accent",
      "sidebar-accent-foreground",
      "sidebar-border",
      "sidebar-ring",
    ],
  },
] as const;

interface ThemeEditorProps {
  onChange?: (theme: ShadcnTheme) => void;
}

export default function ThemeEditor({ onChange }: ThemeEditorProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [theme, setTheme] = useState<ShadcnTheme>({ light: {}, dark: {} });
  const [_originalFormats, setOriginalFormats] = useState<
    Record<string, Record<string, string>>
  >({ light: {}, dark: {} });
  const [mode, setMode] = useState<"light" | "dark">("light");
  const [loading, setLoading] = useState(false);
  const [rawCss, setRawCss] = useState("");

  // Chat functionality
  const { messages, sendMessage, status } = useChat({
    transport: new DefaultChatTransport({
      api: "/api/tinte/chat",
    }),
    onError: (error) => {
      console.error("Chat error:", error);
    },
  });

  // Handle theme application from the Message component
  const handleApplyTheme = useCallback(
    (newTheme: { light: ShadcnTokens; dark: ShadcnTokens }) => {
      console.log("Applying theme:", newTheme);

      setTheme(newTheme);
      onChange?.(newTheme);

      // Update original formats
      setOriginalFormats({
        light: { ...newTheme.light },
        dark: { ...newTheme.dark },
      });
    },
    [onChange],
  );

  // Detect color format
  const detectColorFormat = useCallback(
    (colorValue: string): "hex" | "oklch" | "rgb" | "hsl" | "unknown" => {
      const trimmed = colorValue.trim();
      if (trimmed.startsWith("#")) return "hex";
      if (trimmed.startsWith("oklch(")) return "oklch";
      if (trimmed.startsWith("rgb(")) return "rgb";
      if (trimmed.startsWith("hsl(")) return "hsl";
      return "unknown";
    },
    [],
  );

  // Convert any color to hex for color picker
  const _convertToHex = useCallback((colorValue: string): string => {
    try {
      const trimmed = colorValue.trim();

      // If it's already hex, return it
      if (trimmed.startsWith("#")) {
        return trimmed;
      }

      // For oklch, rgb, hsl - use culori to convert
      const colorObj = oklch(trimmed);
      if (colorObj) {
        const hex = formatHex(colorObj);
        return hex || "#000000";
      }

      return "#000000";
    } catch {
      return "#000000";
    }
  }, []);

  // Convert hex back to original format
  const _convertFromHex = useCallback(
    (hexColor: string, originalValue: string): string => {
      try {
        const format = detectColorFormat(originalValue);

        switch (format) {
          case "hex":
            return hexColor;

          case "oklch": {
            const oklchColor = oklch(hexColor);
            if (oklchColor) {
              const l = oklchColor.l || 0;
              const c = oklchColor.c || 0;
              const h = oklchColor.h || 0;

              // Match the original format style
              if (originalValue.includes(" / ")) {
                // Handle alpha values like "oklch(1 0 0 / 10%)"
                const alphaMatch = originalValue.match(/\/\s*([\d.]+%?)/);
                const alpha = alphaMatch ? ` / ${alphaMatch[1]}` : "";
                return `oklch(${l.toFixed(3)} ${c.toFixed(3)} ${h.toFixed(3)}${alpha})`;
              }
              return `oklch(${l.toFixed(3)} ${c.toFixed(3)} ${h.toFixed(3)})`;
            }
            return originalValue;
          }

          default:
            return hexColor;
        }
      } catch {
        return originalValue;
      }
    },
    [detectColorFormat],
  );

  // Load theme from globals.css
  const loadTheme = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch("/api/tinte/read-globals");
      if (response.ok) {
        const data = await response.json();
        setTheme(data.theme);

        // Store original formats for each token
        const lightFormats: Record<string, string> = {};
        const darkFormats: Record<string, string> = {};

        Object.entries(data.theme.light).forEach(([key, value]) => {
          lightFormats[key] = value as string;
        });

        Object.entries(data.theme.dark).forEach(([key, value]) => {
          darkFormats[key] = value as string;
        });

        setOriginalFormats({ light: lightFormats, dark: darkFormats });
      } else {
        console.error("Failed to load theme from globals.css");
      }
    } catch (error) {
      console.error("Error loading theme:", error);
    }
    setLoading(false);
  }, []);

  // Initialize theme
  useEffect(() => {
    const root = document.documentElement;
    const isDark = root.classList.contains("dark");
    setMode(isDark ? "dark" : "light");
    loadTheme();
  }, [loadTheme]);

  const handleTokenEdit = useCallback(
    (token: string, newValue: string) => {
      setTheme((prev) => {
        const updated = {
          ...prev,
          [mode]: {
            ...prev[mode],
            [token]: newValue,
          },
        };

        onChange?.(updated);
        return updated;
      });

      // Update original formats with new value
      setOriginalFormats((prev) => ({
        ...prev,
        [mode]: {
          ...prev[mode],
          [token]: newValue,
        },
      }));
    },
    [mode, onChange],
  );

  // Sync mode with DOM changes (controlled by next-themes)
  useEffect(() => {
    const observer = new MutationObserver(() => {
      const isDark = document.documentElement.classList.contains("dark");
      setMode(isDark ? "dark" : "light");
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    });

    return () => observer.disconnect();
  }, []);

  // Generate raw CSS from theme
  const generateRawCss = useCallback(() => {
    const lightTokens = Object.entries(theme.light)
      .map(([key, value]) => `  --${key}: ${value};`)
      .join("\n");

    const darkTokens = Object.entries(theme.dark)
      .map(([key, value]) => `  --${key}: ${value};`)
      .join("\n");

    if (!lightTokens && !darkTokens) return "";

    return `:root {\n${lightTokens}\n}\n\n.dark {\n${darkTokens}\n}`;
  }, [theme]);

  // Parse raw CSS and update theme
  const parseRawCss = useCallback(
    (css: string) => {
      try {
        const light: ShadcnTokens = {};
        const dark: ShadcnTokens = {};

        // Match :root block
        const rootMatch = css.match(/:root\s*\{([^}]+)\}/);
        if (rootMatch) {
          const rootContent = rootMatch[1];
          const variableMatches = rootContent.matchAll(
            /--([^:]+):\s*([^;]+);/g,
          );
          for (const match of variableMatches) {
            const key = match[1].trim();
            const value = match[2].trim();
            light[key] = value;
          }
        }

        // Match .dark block
        const darkMatch = css.match(/\.dark\s*\{([^}]+)\}/);
        if (darkMatch) {
          const darkContent = darkMatch[1];
          const variableMatches = darkContent.matchAll(
            /--([^:]+):\s*([^;]+);/g,
          );
          for (const match of variableMatches) {
            const key = match[1].trim();
            const value = match[2].trim();
            dark[key] = value;
          }
        }

        setTheme({ light, dark });
        onChange?.({ light, dark });
      } catch (error) {
        console.error("Failed to parse CSS:", error);
      }
    },
    [onChange],
  );

  // Update raw CSS when theme changes
  useEffect(() => {
    setRawCss(generateRawCss());
  }, [theme, generateRawCss]);

  // Write to globals.css file
  const [saveStatus, setSaveStatus] = useState<
    "idle" | "saving" | "success" | "error"
  >("idle");

  const writeToGlobals = useCallback(async () => {
    setSaveStatus("saving");
    try {
      const lightTokens = Object.entries(theme.light)
        .map(([key, value]) => `  --${key}: ${value};`)
        .join("\n");

      const darkTokens = Object.entries(theme.dark)
        .map(([key, value]) => `  --${key}: ${value};`)
        .join("\n");

      const cssContent = `:root {\n${lightTokens}\n}\n\n.dark {\n${darkTokens}\n}`;

      const response = await fetch("/api/tinte/write-globals", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ css: cssContent }),
      });

      if (response.ok) {
        setSaveStatus("success");
        setTimeout(() => setSaveStatus("idle"), 2000);
      } else {
        const error = await response.json();
        throw new Error(error.error || "Failed to write globals.css");
      }
    } catch (error) {
      console.error("Failed to write globals.css:", error);
      setSaveStatus("error");
      setTimeout(() => setSaveStatus("idle"), 3000);
    }
  }, [theme]);

  const availableTokens = TOKEN_GROUPS.flatMap((group) =>
    group.tokens.filter((token) => theme[mode][token] !== undefined),
  );

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      {/* Floating Ball Trigger */}
      <div className="fixed bottom-4 right-4 z-50">
        <DialogTrigger asChild>
          <button
            type="button"
            className="w-14 h-14 bg-card border-2 border-border rounded-full shadow-lg hover:scale-110 transition-all duration-200 flex items-center justify-center hover:shadow-xl"
            title="Open Theme Editor"
          >
            <Logo size={28} className="drop-shadow-sm" />
          </button>
        </DialogTrigger>
      </div>

      {/* Dialog Content */}
      <DialogContent showCloseButton={false}>
        {/* Header */}
        <DialogHeader>
          <div className="flex items-center gap-3">
            <Logo size={24} />
            <div>
              <DialogTitle>Theme Editor</DialogTitle>
              <p className="text-sm text-muted-foreground">
                Live editing ‚Ä¢ {availableTokens.length} tokens ‚Ä¢ {mode} mode
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <button
              type="button"
              onClick={loadTheme}
              disabled={loading}
              className="p-1.5 hover:bg-accent rounded-md transition-colors disabled:opacity-50"
              title="Reload from globals.css"
            >
              <RefreshCw size={16} className={loading ? "animate-spin" : ""} />
            </button>
            <span className="px-3 py-1.5 text-sm text-muted-foreground border border-border rounded-md">
              Editing: {mode === "light" ? "‚òÄÔ∏è Light" : "üåô Dark"}
            </span>
            <button
              type="button"
              onClick={writeToGlobals}
              disabled={saveStatus === "saving"}
              className="px-3 py-1.5 text-sm bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {saveStatus === "saving" && "Saving..."}
              {saveStatus === "success" && "‚úÖ Saved!"}
              {saveStatus === "error" && "‚ùå Error"}
              {saveStatus === "idle" && "Save CSS"}
            </button>
          </div>
        </DialogHeader>

        {/* Content */}
        <div className="flex-1 overflow-hidden flex flex-col">
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <RefreshCw className="animate-spin mr-2" size={20} />
              <span>Loading theme...</span>
            </div>
          ) : (
            <Tabs
              defaultValue="editor"
              className="flex-1 flex flex-col overflow-hidden"
            >
              <TabsList className="mx-4 mt-4 mb-4">
                <TabsTrigger value="editor">Editor</TabsTrigger>
                <TabsTrigger value="raw">Raw CSS</TabsTrigger>
                <TabsTrigger value="agent">Agent</TabsTrigger>
              </TabsList>

              <TabsContent
                value="editor"
                className="flex-1 h-0 flex flex-col overflow-hidden px-4 pb-4"
              >
                <div className="flex-1 border rounded-md bg-muted/20 overflow-y-auto p-4">
                  <Accordion
                    type="single"
                    collapsible
                    className="w-full space-y-2"
                    defaultValue="Background & Text"
                  >
                    {TOKEN_GROUPS.map((group) => {
                      const groupTokens = group.tokens.filter(
                        (token) => theme[mode][token] !== undefined,
                      );
                      if (groupTokens.length === 0) return null;

                      return (
                        <AccordionItem
                          value={group.label}
                          key={group.label}
                          className="rounded-md border bg-background px-4 py-1 outline-none last:border-b has-focus-visible:border-ring has-focus-visible:ring-[3px] has-focus-visible:ring-ring/50"
                        >
                          <AccordionTrigger className="py-2 text-[15px] leading-6 hover:no-underline focus-visible:ring-0">
                            <span className="uppercase tracking-wide">
                              {group.label} ({groupTokens.length})
                            </span>
                          </AccordionTrigger>
                          <AccordionContent className="pb-2">
                            <div className="grid gap-3 sm:grid-cols-2">
                              {groupTokens.map((token) => (
                                <div key={token} className="space-y-1.5">
                                  <div className="flex items-center justify-between">
                                    <span className="text-xs font-medium text-muted-foreground uppercase tracking-wide">
                                      {token.replace(/-/g, " ")}
                                    </span>
                                    <span className="text-xs text-muted-foreground font-mono">
                                      {detectColorFormat(theme[mode][token])}
                                    </span>
                                  </div>
                                  <ColorInput
                                    value={theme[mode][token]}
                                    onChange={(color) =>
                                      handleTokenEdit(token, color)
                                    }
                                    label={token}
                                  />
                                </div>
                              ))}
                            </div>
                          </AccordionContent>
                        </AccordionItem>
                      );
                    })}
                  </Accordion>
                </div>
              </TabsContent>

              <TabsContent
                value="raw"
                className="flex-1 h-0 flex flex-col overflow-hidden px-4 pb-4"
              >
                <Textarea
                  value={rawCss}
                  onChange={(e) => {
                    setRawCss(e.target.value);
                    parseRawCss(e.target.value);
                  }}
                  className="w-full bg-muted/40 font-mono text-xs resize-none border border-border focus-visible:ring-0 p-4"
                  placeholder="Paste your CSS here..."
                  spellCheck={false}
                  rows={25}
                />
              </TabsContent>

              <TabsContent
                value="agent"
                className="flex-1 h-0 flex flex-col gap-3 overflow-hidden px-4 pb-4"
              >
                <div className="flex-1 border rounded-md bg-muted/20 overflow-y-auto p-4 space-y-2">
                  {messages.length === 0 ? (
                    <div className="flex flex-col items-center justify-center min-h-[380px] gap-6">
                      <div className="text-center space-y-2">
                        <h3 className="font-semibold text-lg">
                          AI Theme Generator
                        </h3>
                        <p className="text-muted-foreground text-sm max-w-md">
                          Describe your ideal theme and let AI generate a
                          complete color palette for you
                        </p>
                      </div>
                      <div className="grid gap-2 w-full max-w-md px-4">
                        <p className="text-xs text-muted-foreground uppercase tracking-wide font-medium">
                          Suggested prompts:
                        </p>
                        <Button
                          variant="outline"
                          onClick={() =>
                            sendMessage({
                              text: "Create a purple theme with high contrast for accessibility",
                            })
                          }
                          className="justify-start h-auto py-3 whitespace-normal text-left"
                        >
                          Create a purple theme with high contrast
                        </Button>
                        <Button
                          variant="outline"
                          onClick={() =>
                            sendMessage({
                              text: "Generate a warm autumn theme with orange and brown tones",
                            })
                          }
                          className="justify-start h-auto py-3 whitespace-normal text-left"
                        >
                          Generate a warm autumn theme
                        </Button>
                        <Button
                          variant="outline"
                          onClick={() =>
                            sendMessage({
                              text: "Create a modern dark theme with blue accents",
                            })
                          }
                          className="justify-start h-auto py-3 whitespace-normal text-left"
                        >
                          Create a modern dark theme with blue accents
                        </Button>
                        <Button
                          variant="outline"
                          onClick={() =>
                            sendMessage({
                              text: "Design a soft pastel theme perfect for a wellness app",
                            })
                          }
                          className="justify-start h-auto py-3 whitespace-normal text-left"
                        >
                          Design a soft pastel wellness theme
                        </Button>
                      </div>
                    </div>
                  ) : (
                    messages.map((message) => (
                      <Message
                        key={message.id}
                        message={message}
                        onApplyTheme={handleApplyTheme}
                      />
                    ))
                  )}
                </div>
                <ChatInput
                  onSubmit={(msg) => {
                    sendMessage({ text: msg });
                  }}
                  disabled={status === "submitted" || status === "streaming"}
                />
              </TabsContent>
            </Tabs>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import type React from "react";
import { useCallback, useEffect, useMemo, useState } from "react";

import { hexToHsva, hsvaToHex } from "@uiw/color-convert";
import { Colorful } from "@uiw/react-color";
import { hsl as culoriHsl, formatHex, lch, oklch, rgb } from "culori";
import { ChevronDown } from "lucide-react";

import { cn } from "@/lib/utils";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

interface ColorInputProps {
  value: string;
  onChange: (value: string) => void;
  label: string;
  disabled?: boolean;
}

const getContrastColor = (hexColor: string): string => {
  const hex = hexColor.replace("#", "");
  const r = parseInt(hex.substring(0, 2), 16);
  const g = parseInt(hex.substring(2, 4), 16);
  const b = parseInt(hex.substring(4, 6), 16);
  const brightness = (r * 299 + g * 587 + b * 114) / 1000;
  return brightness > 128 ? "#000000" : "#ffffff";
};

const toHex = (colorValue: string): string => {
  try {
    if (colorValue.startsWith("#")) return colorValue;
    if (colorValue.startsWith("oklch(")) {
      const parsed = oklch(colorValue);
      return parsed ? formatHex(parsed) : "#000000";
    }
    if (colorValue.startsWith("lch(")) {
      const parsed = lch(colorValue);
      return parsed ? formatHex(parsed) : "#000000";
    }
    if (colorValue.startsWith("hsl(")) {
      const parsed = culoriHsl(colorValue);
      return parsed ? formatHex(parsed) : "#000000";
    }
    if (colorValue.startsWith("rgb(")) {
      const parsed = rgb(colorValue);
      return parsed ? formatHex(parsed) : "#000000";
    }
    return "#000000";
  } catch {
    return "#000000";
  }
};

const detectFormat = (
  colorValue: string,
): "hex" | "rgb" | "hsl" | "oklch" | "lch" => {
  if (colorValue.startsWith("oklch(")) return "oklch";
  if (colorValue.startsWith("lch(")) return "lch";
  if (colorValue.startsWith("hsl(")) return "hsl";
  if (colorValue.startsWith("rgb(")) return "rgb";
  return "hex";
};

const fromHex = (
  hexValue: string,
  format: "hex" | "rgb" | "hsl" | "oklch" | "lch",
): string => {
  try {
    const rgbColor = rgb(hexValue);
    if (!rgbColor) return hexValue;
    switch (format) {
      case "hex":
        return hexValue;
      case "rgb": {
        const r = Math.round(rgbColor.r * 255);
        const g = Math.round(rgbColor.g * 255);
        const b = Math.round(rgbColor.b * 255);
        return `rgb(${r} ${g} ${b})`;
      }
      case "hsl": {
        const hslColor = culoriHsl(hexValue);
        if (!hslColor) return hexValue;
        const h = Math.round(hslColor.h || 0);
        const s = Math.round((hslColor.s || 0) * 100);
        const l = Math.round((hslColor.l || 0) * 100);
        return `hsl(${h} ${s}% ${l}%)`;
      }
      case "oklch": {
        const oklchColor = oklch(hexValue);
        if (!oklchColor) return hexValue;
        const l = (oklchColor.l || 0).toFixed(3);
        const c = (oklchColor.c || 0).toFixed(3);
        const h = Math.round(oklchColor.h || 0);
        return `oklch(${l} ${c} ${h})`;
      }
      case "lch": {
        const lchColor = lch(hexValue);
        if (!lchColor) return hexValue;
        const l = Math.round(lchColor.l || 0);
        const c = Math.round(lchColor.c || 0);
        const h = Math.round(lchColor.h || 0);
        return `lch(${l} ${c} ${h})`;
      }
      default:
        return hexValue;
    }
  } catch {
    return hexValue;
  }
};

export function ColorInput({
  value,
  onChange,
  label,
  disabled = false,
}: ColorInputProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [inputValue, setInputValue] = useState(value);
  const [activeTab, setActiveTab] = useState("hex");
  const originalFormat = useMemo(() => detectFormat(value), [value]);

  useEffect(() => {
    setInputValue(value);
  }, [value]);

  const hexColor = useMemo(() => toHex(inputValue), [inputValue]);
  const contrastColor = useMemo(() => {
    if (!/^#[0-9A-Fa-f]{6}$/.test(hexColor)) return "#000000";
    return getContrastColor(hexColor);
  }, [hexColor]);

  const colorValues = useMemo(() => {
    if (!/^#[0-9A-Fa-f]{6}$/.test(hexColor)) {
      return {
        hex: "#000000",
        rgb: { r: 0, g: 0, b: 0 },
        hsl: { h: 0, s: 0, l: 0 },
        oklch: { l: 0, c: 0, h: 0 },
        lch: { l: 0, c: 0, h: 0 },
      };
    }
    const rgbColor = rgb(hexColor);
    const hslColor = culoriHsl(hexColor);
    const oklchColor = oklch(hexColor);
    const lchColor = lch(hexColor);
    return {
      hex: hexColor,
      rgb: rgbColor
        ? {
            r: Math.round(rgbColor.r * 255),
            g: Math.round(rgbColor.g * 255),
            b: Math.round(rgbColor.b * 255),
          }
        : { r: 0, g: 0, b: 0 },
      hsl: hslColor
        ? {
            h: Math.round(hslColor.h || 0),
            s: Math.round((hslColor.s || 0) * 100),
            l: Math.round((hslColor.l || 0) * 100),
          }
        : { h: 0, s: 0, l: 0 },
      oklch: oklchColor
        ? {
            l: Math.round((oklchColor.l || 0) * 100),
            c: Math.round((oklchColor.c || 0) * 100),
            h: Math.round(oklchColor.h || 0),
          }
        : { l: 0, c: 0, h: 0 },
      lch: lchColor
        ? {
            l: Math.round(lchColor.l || 0),
            c: Math.round(lchColor.c || 0),
            h: Math.round(lchColor.h || 0),
          }
        : { l: 0, c: 0, h: 0 },
    };
  }, [hexColor]);

  const handleInputChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const newValue = e.target.value;
      setInputValue(newValue);
      if (/^#[0-9A-Fa-f]{6}$/.test(newValue)) {
        onChange(fromHex(newValue, originalFormat));
      }
    },
    [onChange, originalFormat],
  );

  const handleInputBlur = useCallback(() => {
    if (!/^#[0-9A-Fa-f]{6}$/.test(inputValue)) {
      setInputValue(value);
    }
  }, [inputValue, value]);

  const handleColorfulChange = useCallback(
    (newColor: { hsva: { h: number; s: number; v: number; a: number } }) => {
      const hex = hsvaToHex(newColor.hsva);
      setInputValue(hex);
      onChange(fromHex(hex, originalFormat));
    },
    [onChange, originalFormat],
  );

  const handleQuickColorClick = useCallback(
    (quickColor: string) => {
      setInputValue(quickColor);
      onChange(fromHex(quickColor, originalFormat));
    },
    [onChange, originalFormat],
  );

  const handleColorSpaceChange = useCallback(
    (values: Record<string, number>, colorSpace: string) => {
      let newHex: string | undefined;
      switch (colorSpace) {
        case "rgb":
          newHex = formatHex(
            rgb({
              mode: "rgb",
              r: values.r / 255,
              g: values.g / 255,
              b: values.b / 255,
            }),
          );
          break;
        case "hsl":
          newHex = formatHex(
            culoriHsl({
              mode: "hsl",
              h: values.h,
              s: values.s / 100,
              l: values.l / 100,
            }),
          );
          break;
        case "oklch":
          newHex = formatHex(
            oklch({
              mode: "oklch",
              l: values.l / 100,
              c: values.c / 100,
              h: values.h,
            }),
          );
          break;
        case "lch":
          newHex = formatHex(
            lch({ mode: "lch", l: values.l, c: values.c, h: values.h }),
          );
          break;
        default:
          return;
      }
      if (newHex && /^#[0-9A-Fa-f]{6}$/.test(newHex)) {
        setInputValue(newHex);
        onChange(fromHex(newHex, originalFormat));
      }
    },
    [onChange, originalFormat],
  );

  const hsvaColor = useMemo(() => {
    if (!/^#[0-9A-Fa-f]{6}$/.test(hexColor)) {
      return { h: 0, s: 0, v: 0, a: 1 };
    }
    return hexToHsva(hexColor);
  }, [hexColor]);

  const quickColors = useMemo(
    () => [
      "#000000",
      "#ffffff",
      "#ef4444",
      "#22c55e",
      "#3b82f6",
      "#f59e0b",
      "#a855f7",
      "#ec4899",
    ],
    [],
  );

  return (
    <div className="flex items-center gap-3 py-2">
      <Popover
        open={isOpen && !disabled}
        onOpenChange={(open) => !disabled && setIsOpen(open)}
      >
        <PopoverTrigger asChild>
          <Button
            variant="outline"
            className={cn(
              "h-10 justify-between font-mono text-sm w-full",
              disabled && "opacity-50 cursor-not-allowed",
            )}
            disabled={disabled}
            style={{ backgroundColor: hexColor, color: contrastColor }}
          >
            <span className="truncate">{inputValue}</span>
            <ChevronDown className="ml-2 h-4 w-4 shrink-0 opacity-70" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-[320px] p-0" align="end" sideOffset={8}>
          <div className="p-4 space-y-4">
            <Colorful
              color={hsvaColor}
              onChange={handleColorfulChange}
              disableAlpha={true}
              style={{ width: "100%" }}
            />

            <Tabs
              value={activeTab}
              onValueChange={setActiveTab}
              className="w-full"
            >
              <TabsList className="grid w-full grid-cols-5 h-9">
                <TabsTrigger value="hex" className="text-xs">
                  HEX
                </TabsTrigger>
                <TabsTrigger value="rgb" className="text-xs">
                  RGB
                </TabsTrigger>
                <TabsTrigger value="hsl" className="text-xs">
                  HSL
                </TabsTrigger>
                <TabsTrigger value="oklch" className="text-xs">
                  OKLCH
                </TabsTrigger>
                <TabsTrigger value="lch" className="text-xs">
                  LCH
                </TabsTrigger>
              </TabsList>

              <TabsContent value="hex" className="mt-3 space-y-0">
                <Input
                  value={inputValue}
                  onChange={handleInputChange}
                  onBlur={handleInputBlur}
                  placeholder="#000000"
                  className="font-mono text-sm h-9"
                  disabled={disabled}
                />
              </TabsContent>

              <TabsContent value="rgb" className="mt-3 space-y-0">
                <div className="grid grid-cols-3 gap-2">
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">R</Label>
                    <Input
                      type="number"
                      value={colorValues.rgb.r}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.rgb, r: Number(e.target.value) },
                          "rgb",
                        )
                      }
                      min="0"
                      max="255"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">G</Label>
                    <Input
                      type="number"
                      value={colorValues.rgb.g}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.rgb, g: Number(e.target.value) },
                          "rgb",
                        )
                      }
                      min="0"
                      max="255"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">B</Label>
                    <Input
                      type="number"
                      value={colorValues.rgb.b}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.rgb, b: Number(e.target.value) },
                          "rgb",
                        )
                      }
                      min="0"
                      max="255"
                      className="h-9 text-sm"
                    />
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="hsl" className="mt-3 space-y-0">
                <div className="grid grid-cols-3 gap-2">
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">H</Label>
                    <Input
                      type="number"
                      value={colorValues.hsl.h}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.hsl, h: Number(e.target.value) },
                          "hsl",
                        )
                      }
                      min="0"
                      max="360"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">S</Label>
                    <Input
                      type="number"
                      value={colorValues.hsl.s}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.hsl, s: Number(e.target.value) },
                          "hsl",
                        )
                      }
                      min="0"
                      max="100"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">L</Label>
                    <Input
                      type="number"
                      value={colorValues.hsl.l}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.hsl, l: Number(e.target.value) },
                          "hsl",
                        )
                      }
                      min="0"
                      max="100"
                      className="h-9 text-sm"
                    />
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="oklch" className="mt-3 space-y-0">
                <div className="grid grid-cols-3 gap-2">
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">L</Label>
                    <Input
                      type="number"
                      value={colorValues.oklch.l}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.oklch, l: Number(e.target.value) },
                          "oklch",
                        )
                      }
                      min="0"
                      max="100"
                      step="0.1"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">C</Label>
                    <Input
                      type="number"
                      value={colorValues.oklch.c}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.oklch, c: Number(e.target.value) },
                          "oklch",
                        )
                      }
                      min="0"
                      max="100"
                      step="0.1"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">H</Label>
                    <Input
                      type="number"
                      value={colorValues.oklch.h}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.oklch, h: Number(e.target.value) },
                          "oklch",
                        )
                      }
                      min="0"
                      max="360"
                      className="h-9 text-sm"
                    />
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="lch" className="mt-3 space-y-0">
                <div className="grid grid-cols-3 gap-2">
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">L</Label>
                    <Input
                      type="number"
                      value={colorValues.lch.l}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.lch, l: Number(e.target.value) },
                          "lch",
                        )
                      }
                      min="0"
                      max="100"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">C</Label>
                    <Input
                      type="number"
                      value={colorValues.lch.c}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.lch, c: Number(e.target.value) },
                          "lch",
                        )
                      }
                      min="0"
                      max="150"
                      className="h-9 text-sm"
                    />
                  </div>
                  <div className="space-y-1.5">
                    <Label className="text-xs text-muted-foreground">H</Label>
                    <Input
                      type="number"
                      value={colorValues.lch.h}
                      onChange={(e) =>
                        handleColorSpaceChange(
                          { ...colorValues.lch, h: Number(e.target.value) },
                          "lch",
                        )
                      }
                      min="0"
                      max="360"
                      className="h-9 text-sm"
                    />
                  </div>
                </div>
              </TabsContent>
            </Tabs>

            <div className="space-y-2">
              <Label className="text-xs text-muted-foreground">
                Quick Colors
              </Label>
              <div className="grid grid-cols-8 gap-2">
                {quickColors.map((quickColor) => (
                  <button
                    key={quickColor}
                    type="button"
                    className="w-full aspect-square rounded border-2 border-border hover:border-foreground hover:scale-110 transition-all"
                    style={{ backgroundColor: quickColor }}
                    onClick={() => handleQuickColorClick(quickColor)}
                    disabled={disabled}
                    aria-label={`Quick color ${quickColor}`}
                  />
                ))}
              </div>
            </div>
          </div>
        </PopoverContent>
      </Popover>
    </div>
  );
}

import { readFile } from "node:fs/promises";
import { join } from "node:path";

import { NextResponse } from "next/server";

export async function GET() {
  try {
    const globalsPath = join(process.cwd(), "app", "globals.css");
    const content = await readFile(globalsPath, "utf-8");

    return NextResponse.json({ success: true, content });
  } catch (error) {
    console.error("Error reading globals.css:", error);
    return NextResponse.json(
      { success: false, error: "Failed to read globals.css" },
      { status: 500 },
    );
  }
}

import { writeFile } from "node:fs/promises";
import { join } from "node:path";

import { NextResponse } from "next/server";

export async function POST(request: Request) {
  try {
    const { content } = await request.json();

    if (typeof content !== "string") {
      return NextResponse.json(
        { success: false, error: "Invalid content" },
        { status: 400 },
      );
    }

    const globalsPath = join(process.cwd(), "app", "globals.css");
    await writeFile(globalsPath, content, "utf-8");

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error("Error writing globals.css:", error);
    return NextResponse.json(
      { success: false, error: "Failed to write globals.css" },
      { status: 500 },
    );
  }
}
